import React, { useState,useContext } from 'react';
import AddressContext from '../addresscontextapi/addressstate'
const Addressform = () => {
    const addresscontext = useContext(AddressContext);
    const {addresstate,addaddress} = addresscontext;
    console.log(addresstate);
    const [formdata,setformdata] = useState({
        address:''
    })

     const {address} = formdata; 
  const onSubmit = (e)=>{
      e.preventDefault();
      addaddress({address});
  }  
    return (  
        <div>
            <form  onSubmit={e=>onSubmit(e)} >
            <div class="form-group">
    <h3><label>Enter Your Shipping Address</label></h3>
    <textarea class="form-control" id="exampleFormControlTextarea1" rows="3" value={formdata.address} onChange = {(e=>setformdata({...formdata,address:e.target.value}))}></textarea>
   
  </div>
  <button type="submit" className=" rounded btn btn-outline-primary">Submit</button>
            </form>
        </div>
    );
}
 
export default Addressform;
