import React,{useState,useContext} from 'react';
import ProductContext from '../productcontextapi/productcontext'
const Search = () => {
    const productcontext = useContext(ProductContext)
    const {productstate,search}= productcontext;
    console.log(productstate)
    const [formdata ,setformdata] = useState({
        key:''
    })
    console.log(formdata.key)
 const onSubmit = (e)=>{
      e.preventDefault();
      search(formdata.key);
 }
    return (
        <form className="form-inline" onSubmit={(e)=>onSubmit(e)}>
        <input style={{height:"35px"}} className="form-control p-1 rounded-pill border border-dark mr-4 w-55" type="search" placeholder="Search" aria-label="Search" value={formdata.key} onChange={(e)=>setformdata({...key,key:e.target.value})} />
        <button className="btn btn-outline-success btn-sm rounded mt-1" type="submit">Search</button>
        </form>
      );
}
 
export default Search;
