import {
      GET_PRODUCTS,
      PRODUCT_ERROR
} from '../types'



export default (state,action) => {
    switch(action.type)
    {
        case GET_PRODUCTS:
            return {
                ...state,
                loading:false,
                products:action.payload
            }
        case PRODUCT_ERROR:
            return {
                ...state,
                loading:false,
             
            }
            default:
                return state; 
    }
}
