import React from 'react';
import Moment from 'react-moment';
import {Link} from 'react-router-dom'
const AllOrderItems = ({order}) => {
    return ( 
      <div className="row">
        <div className="col">
        <div class="card-deck ">
  <div class="card">
    
    <div class="card-body">
      <h5 class="card-title">Order Id: {order._id}</h5>
      <h5 class="card-title">{order.user}</h5>
      {order.isPaid ? (
         <h5 class="card-title">Paid: <i class="fa fa-check-double"></i></h5>
      ):(
        <h5 class="card-title">Paid: Not Paid</h5>  
      )}
      {order.isDelivered ? (
         <h5 class="card-title">Delivered: <i class="fa fa-check-double"></i></h5>
      ):(
        <h5 class="card-title">Delivered: Not Delivered</h5>  
      )}
      <h5 class="card-title"><Moment format='YYYY/MM/DD'>{order.date}</Moment></h5>  
      <h5 class="card-title">Total:{order.totalPrice}</h5>  
      <Link to={`/${order._id}`} className="btn btn-info rounded-pill">Details</Link>
    </div>
    
  </div>
  
</div>
</div>
</div>
     );
}
 
export default AllOrderItems;
