import React,{useContext, useEffect, useState} from 'react';
import AuthContext from '../authcontextapi/authcontext';
import setAuthToken from '../utils/setAuthToken';
import { useAlert } from 'react-alert'
const Register = () => {
    const authcontext = useContext(AuthContext);
    const {register,authstate,loaduser} = authcontext;
    const alert = useAlert()
    const [formdata,setformdata] = useState({
         name:'',
         email:'',
         password:'',
         password2:''


    });
    const {name,email,password,password2} = formdata;
 console.log(authstate);
 useEffect(()=>{
     if(authstate.error.length>0)
     {
         authstate.error.forEach(err=>{
             alert.show(`${err.msg}`);
         })
     }
 },[authstate.error,alert])

 useEffect(()=>{
     console.log(localStorage.token);
      if(localStorage.token)
      {
          setAuthToken(localStorage.token)
          loaduser();
      }
  
     
  },[authstate.token])// eslint-disable-line react-hooks/exhaustive-deps


    const onSubmit = (e)=>{
       
        e.preventDefault();
      
        if(password!==password2)
        {
            alert.show('passwords do not match');
        }
        else{
            register({name ,email,password});
        }
        
    }   
    
 
      
            
     
    return ( 
        <React.Fragment>
             <div className="jumbotron rounded container mt-5 bg-light"
          >
        <center>
       <form className="container pt-5" onSubmit={e=>onSubmit(e)}>
                <h3 className=" display-4">Register</h3>
            
                <div className="form-group col-lg-7">
                    <h4><label className="text-info"> Name</label></h4>
                    <input  type="text" className="form-control" placeholder="Enter Name" value={formdata.name} onChange={(e=>setformdata({...formdata,name:e.target.value}))}  />
                </div>
                
                

                <div className="form-group col-lg-7">
                <h4> <label className="text-info">Email</label></h4>
                    <input  type="email" className="form-control" placeholder="Enter email"  value={formdata.email} onChange={e=>setformdata({...formdata,email:e.target.value})}/>
                </div>

                <div className="form-group col-lg-7">
                <h4><label className="text-info">Password</label></h4>
                    <input  type="password" className="form-control" placeholder="Enter password" value={formdata.password} onChange={e=>setformdata({...formdata,password:e.target.value})}/>
                </div>
                <div className="form-group col-lg-7">
                <h4><label className="text-info">Repeat Password</label></h4>
                    <input  type="password" className="form-control" placeholder="Enter password" value={formdata.password2} onChange={e=>setformdata({...formdata,password2:e.target.value})}/>
                </div>

                <button type="submit" className=" rounded btn btn-outline-info ">Register</button>
                
            </form>         
            </center>
   
</div>
        </React.Fragment>
     );
}
 
export default Register;
