import React,{useContext,useEffect,useState} from 'react';
import { Link } from 'react-router-dom';
import AddressContext from '../addresscontextapi/addresscontext'
import AuthContext from '../authcontextapi/authcontext'
const AddressList = ({address}) => {
     const [isShown, setIsShown] = useState(false);
     const authcontext = useContext(AuthContext);
    const {authstate}= authcontext;
    console.log(authstate)
    const addresscontext = useContext(AddressContext);
    const {addressstate,deleteaddress,getaddress} = addresscontext;
    console.log(addressstate);

    const deleteadd = (index)=>{
             deleteaddress(authstate.user._id,index);
           
        
            
    
          }
     
         
        
          
     
    return ( 
         <h4>
       
        <ul className="list-group">
        <li onMouseEnter={() => setIsShown(true)}
        className="list-group-item text-info rounded mt-2" aria-disabled="true">{address}</li>
            
          
        <div      className="row">  
             {
                  isShown && (
                    <React.Fragment>
                    <div className="col ">
                    <span><button onMouseLeave={() => setIsShown(false)}  onClick={()=>deleteadd(address.key)} className=" rounded btn btn-danger btn-sm mt-2"><Link><i className="fa fa-minus-square text-light"></i></Link></button>
              </span>
                    </div>  
                    <div className="col">
                    <span><button className=" rounded btn btn-info btn-sm mt-2"><i className="fa fa-check"></i></button>
              </span>
                    </div> 
                    
              </React.Fragment>
                  )
             }
        </div>  
          
      
      </ul>
        
      </h4>       


     );
}
 
export default AddressList;
