import {
     CREATE_ORDER,
     ALL_ORDERS,
     PER_ORDER,
     GET_USER
}from '../types'



export default (state,action)=>{

     switch(action.type)
     {
         case CREATE_ORDER:
         case PER_ORDER:
         return {
             ...state,
             order:action.payload
         }
         case GET_USER:
         case ALL_ORDERS:
             return {
                 ...state,
                 orders:action.payload
             }
         default:
             return state;
     }







}
