import React, { useState,useContext,useEffect } from 'react';

import AuthContext from '../authcontextapi/authcontext';
import setAuthToken from '../utils/setAuthToken';
import { useAlert } from 'react-alert';
import {Redirect} from 'react-router-dom';


const Login = () => {
    const authcontext = useContext(AuthContext);
    const {login,authstate,loaduser} = authcontext;
    const alert = useAlert()
      const [formdata,setformdata] = useState({
            email:'',
            password:''
 

      })
    
      const {email,password} = formdata;
      console.log(authstate);
      useEffect(()=>{
          if(authstate.error.length>0)
          {
              authstate.error.forEach(err=>{
                  alert.show(`${err.msg}`);
              })
          }
      },[authstate.error,alert])
     
       useEffect(()=>{
           console.log(localStorage.token);
            if(localStorage.token)
            {
               setAuthToken(localStorage.token)
               loaduser();
            }
        
        },[authstate.token])// eslint-disable-line react-hooks/exhaustive-deps
     
     
         const onSubmit = (e)=>{
            
             e.preventDefault();
           
           
   
                 login({email,password});
       
             
         }    
//redirect 
  if(authstate.isAuthenticated)
    {
      return <Redirect to='/home'/>
  }


    return ( 
        
          <div className="jumbotron box rounded  mt-5 bg-light "
        
          
          >
        
       
                <center>
                <h1 >Login</h1>
       <form className="container pt-5" onSubmit={e=>onSubmit(e)}>
                
            
              
                
                

                <div className="form-group col-lg-7">
                    <h4><label className="text-info">Email</label></h4>
                    <input  type="email" className="form-control" placeholder="Enter email" value={formdata.email} onChange={e=>setformdata({...formdata,email:e.target.value})}/>
                </div>

                <div className="form-group col-lg-7">
                   <h4> <label className="text-info">Password</label></h4>
                    <input  type="password" className="form-control" placeholder="Enter password" value={formdata.password} onChange={e=>setformdata({...formdata,password:e.target.value})}/>
                </div>

                <button type="submit" className="  rounded btn btn-outline-info ">Login</button>
                
            </form>
            
       
                
          
           
           
            </center>
</div>
        
     );
}
 
export default Login;
