import React ,{useContext,useEffect}from 'react';
import AllOrderItems from './allorderitems'
import OrderContext from '../ordercontextapi/ordercontext'
const Orders = () => {
    const ordercontext  = useContext(OrderContext);
  const {orderstate,getorders}= ordercontext;
  console.log(orderstate);
  useEffect(()=>{
      getorders()
  },[])
    return ( 
        <React.Fragment>
            <div className="container">
                <div className="row">
                    <div className="col">
                    <div className="card text-white bg-secondary mb-3" style="max-width: 18rem;">
                    <div className="card-header">Header</div>
                    <div class="card-body">
                    <h5 class="card-title">Secondary card title</h5>
                    </div>
                    </div>
                    </div>
                    <div className="col">

                    </div>
                    <div className="col">

                    </div>
                </div>

            </div>
        <div className="container">
        <h1>All Orders</h1>
        <div className="row">
           {orderstate.orders.map((order)=>(
                <AllOrderItems key={order._id} order={order}/>
           ))
        }
        </div>
         </div>
         </React.Fragment>
     );
}
 
export default Orders;
