import React,{useContext,useEffect} from 'react';
import { Link,Redirect } from 'react-router-dom';
import AuthContext from '../authcontextapi/authcontext';
import ProductContext from '../productcontextapi/productcontext'
const Navbar = () => {

    


    const productcontext = useContext(ProductContext); 
    const {getproducts,productstate} = productcontext;
    const authcontext = useContext(AuthContext);
    const {authstate,logout} = authcontext;
    useEffect(()=>{
      getproducts();
  },[])
    
    const authlinks = (
             <React.Fragment>
                 <li className="nav-item active">
      <a onClick={logout}  className="nav-link " ><i className="fa fa-sign-in" aria-hidden="true"></i>
     logout</a>
    </li>
    if(authstate.isadmin)
    <li className="nav-item active">
      <Link to= "/admin" className="nav-link " ><i className="fa fa-admin" aria-hidden="true"></i>
     Admin</Link>
    </li>
             </React.Fragment>
    );
    const guestlinks = (
      <React.Fragment>
      <li className="nav-item active">
      <Link to= "/login" className="nav-link " ><i className="fa fa-sign-in" aria-hidden="true"></i>
     login</Link>
    </li>
    <li className="nav-item active">
      <Link to= "/register" className="nav-link " ><i className="fa fa-sign-in" aria-hidden="true"></i>
     register</Link>
    </li>
    <li className="nav-item active">
      <Link to= '/cart'className="nav-link " ><i className="fa fa-shopping-cart" aria-hidden="true"></i>
cart</Link>
    </li>
     </React.Fragment>
    );





    return (
      
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        
  <button className="navbar-toggler navbar-brand" type="button" data-toggle="collapse" data-target="#navbarColor02" aria-controls="navbarColor02" aria-expanded="false" aria-label="Toggle navigation">
   <i class="fa fa-bars" aria-hidden="true"></i>

  </button>
  <Link className="navbar-brand mx-auto" to="/"><i class="fa fa-pagelines" aria-hidden="true"></i>
Dimestore</Link>
 

  <div className="collapse navbar-collapse" id="navbarColor02">
    
    <ul className="navbar-nav mr-auto">
      <li className="nav-item active ml-3">
        <Link to='/home'className="nav-link" ><i className="fa fa-home"></i>Home
          <span className="sr-only">(current)</span>
        </Link>
      </li>
      <li class="nav-item dropdown active">
    <a class="nav-link dropdown-toggle" data-toggle="dropdown" href="#" role="button" aria-haspopup="true" aria-expanded="false">Category</a>
    <div class="dropdown-menu bg-light" >
         {productstate.products.map((prod)=>(
                <Link className="dropdown-item " to={`/category/${prod.category}`}>{prod.category}</Link>
         ))}
      
     
      
    </div>
  </li>
     
     
     
    </ul>
    <ul className="navbar-nav ml-auto">
       <li className="nav-item active">
        <a className="nav-link " ><i className="fa fa-info" aria-hidden="true"></i>
About</a>
      </li>
      {authstate.isAuthenticated?authlinks:guestlinks}
    </ul>
  </div>
</nav>

      );
}
 
export default Navbar;
