import React,{useContext,useEffect} from 'react';

import Product from './product';
import ProductContext from '../productcontextapi/productcontext'
import Carousel from './carousel'
const ProductsList = () => {
        const productcontext = useContext(ProductContext); 
        const {getproducts,productstate} = productcontext;
       
        useEffect(()=>{
            getproducts();
        },[])
        console.log(productstate)
    return (  
        <React.Fragment>
         <div id="carouselExampleFade" className=" container-fluid mt-2 carousel slide carousel-fade" data-ride="carousel">
  <div class="carousel-inner">

      {productstate.products.for((prod)=>(
                  <div class="carousel-item active">
                  <img style={{height:"500px"}} src={prod.image.base64} className="d-block w-100" alt="..."/>
                </div>
              
      ))}
    
  </div>
 
</div>
               
         <div className="container mt-3 ">
 
             <h1  >Latest Products</h1>
             <div className="row">
                  {productstate.products.length>0 ? (
                      productstate.products.map((prod)=>(
                          <Product key={prod._id} product={prod}/>
                          
                      ))   
                        

                  ):(
                      <h3> no products found</h3>
                  )}
                  

                  
             </div>
             </div>
      
          
             </React.Fragment>


    );
}
 
export default ProductsList;
