import React,{useContext,useEffect} from 'react';

import Product from './product';
import ProductContext from '../productcontextapi/productcontext'
const ProductsList = () => {
        const productcontext = useContext(ProductContext); 
        const {getproducts,productstate} = productcontext;
       
        useEffect(()=>{
            getproducts();
        },[])
        console.log(productstate)
    return (  
        <React.Fragment>
                 <div className="container">
                 <div  id="carouselExampleFade" class="carousel slide carousel-fade" data-ride="carousel">
  <div  class="carousel-inner">
    <div class="carousel-item active">
      <img src="https://i.guim.co.uk/img/media/953860ce3e8f24da59a4a0173383eb95f9384c78/141_21_810_486/master/810.jpg?width=1200&quality=85&auto=format&fit=max&s=4b7289aa83a0ca6a38868e2eff666feb" class="d-block w-100" alt="..."/>
    </div>
    <div class="carousel-item">
      <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTJfsNsDMHf6B34m774TLKsQNpy2Ua83Yy0lA&usqp=CAU" class="d-block w-100" alt="..."/>
    </div>
    <div class="carousel-item">
      <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQqv7vWMPyrRnq6b7EOxf_Dn1rRxK9a02yBCA&usqp=CAU" class="d-block w-100" alt="..."/>
    </div>
  </div>
  <a class="carousel-control-prev" href="#carouselExampleFade" role="button" data-slide="prev">
    <span class="carousel-control-prev-icon" aria-hidden="true"></span>
    <span class="sr-only">Previous</span>
  </a>
  <a class="carousel-control-next" href="#carouselExampleFade" role="button" data-slide="next">
    <span class="carousel-control-next-icon" aria-hidden="true"></span>
    <span class="sr-only">Next</span>
  </a>
</div>
                 </div>
         <div className="container mt-3 ">
          
             <h1  >Latest Products</h1>
             <div className="row">
                  {productstate.products.length>0 ? (
                      productstate.products.map((prod)=>(
                          <Product key={prod._id} product={prod}/>
                          
                      ))   
                        

                  ):(
                      <h3> no products found</h3>
                  )}
             </div>
             </div>
      
          
             </React.Fragment>


    );
}
 
export default ProductsList;
