import {
      GET_PRODUCTS,
      PRODUCT_ERROR,
      PER_PRODUCT,
      CATEGORY_PRODUCT,
      CART_PRODUCTS,
      GET_USER_ADDRESS,
      ADD_PRODUCT,
      UPDATED_PRODUCT,
      DELETED_PRODUCT
} from '../types'



export default (state,action) => {
    switch(action.type)
    {
        case GET_PRODUCTS:
            return {
                ...state,
                loading:false,
                products:action.payload
            }
            case PER_PRODUCT:
            return {
                ...state,
                loading:false,
                product:action.payload
            }
            case CATEGORY_PRODUCT:
            return {
                ...state,
                loading:false,
                categoryproducts:action.payload
            }
        case PRODUCT_ERROR:
            return {
                ...state,
                loading:false,
                errors:action.payload
            }
        case CART_PRODUCTS:
            return{
                ...state,
                loading:false,
                cart:[...state.cart,action.payload]
            }
        case GET_USER_ADDRESS:
            return {
                ...state,
                loading:false,
                usercartaddress:action.payload
            }
        case ADD_PRODUCT:
            return {
                ...state,
                addedproduct:action.payload
            }
        case UPDATED_PRODUCT:
            return {
                ...state,
                updatedproduct:action.payload
            }  
        case DELETED_PRODUCT:
            return{
                ...state,
                deletedproduct:action.payload
            }  
            default:
                return state; 
    }
}
