import React,{useContext,useEffect} from 'react';
import '@splidejs/splide/dist/css/themes/splide-default.min.css';

import { Splide, SplideSlide } from '@splidejs/react-splide';
import Product from './product';
import ProductContext from '../productcontextapi/productcontext'

const ProductsList = () => {
        const productcontext = useContext(ProductContext); 
        const {getproducts,productstate,cat} = productcontext;
       
        useEffect(()=>{
            getproducts();
        },[])
        useEffect(()=>{
            cat("606765e2cc4f5b486c95530b");
        },[])
        console.log(productstate)
    return (  
        <React.Fragment>
      <div className="">
      <Splide
         options={ {
            type    : 'loop',
            perPage : 1,
            autoWidth: true,
	      autoplay: true,
          interval:2000,
          focus    : 'center',
          autoHeight:true,
     
       
	      rewind: true,
          } }
      >
          {productstate.products.map((prod)=>(
                <SplideSlide>
                <img src={prod.image.base64} alt="Image 1"/>
              </SplideSlide>
              
          ))}
  
</Splide>
</div>
   
               
         <div className="container mt-3 ">
           <h1>Categories</h1>
           <div id="carouselExampleFade" class="carousel slide carousel-fade" data-ride="carousel">
  <div class="carousel-inner">

      {products}
    <div class="carousel-item active">
        
    </div>
    
  </div>
  <a class="carousel-control-prev" href="#carouselExampleFade" role="button" data-slide="prev">
    <span class="carousel-control-prev-icon" aria-hidden="true"></span>
    <span class="sr-only">Previous</span>
  </a>
  <a class="carousel-control-next" href="#carouselExampleFade" role="button" data-slide="next">
    <span class="carousel-control-next-icon" aria-hidden="true"></span>
    <span class="sr-only">Next</span>
  </a>
</div>
            
             <h1  >Latest Products</h1>
             <div className="row">
                  {productstate.products.length>0 ? (
                      productstate.products.map((prod)=>(
                          <Product key={prod._id} product={prod}/>
                          
                      ))   
                        

                  ):(
                      <h3> no products found</h3>
                  )}
                  

                  
             </div>
             </div>
      
          
             </React.Fragment>


    );
}
 
export default ProductsList;
