import {
      GET_PRODUCTS,
      PRODUCT_ERROR,
      PER_PRODUCT
} from '../types'



export default (state,action) => {
    switch(action.type)
    {
        case GET_PRODUCTS:
            return {
                ...state,
                loading:false,
                products:action.payload
            }
            case PER_PRODUCT:
            return {
                ...state,
                loading:false,
                product:action.payload
            }
        case PRODUCT_ERROR:
            return {
                ...state,
                loading:false,
                errors:action.payload
            }
            default:
                return state; 
    }
}
