import {
      GET_PRODUCTS,
      PRODUCT_ERROR,
      PER_PRODUCT,
      CATEGORY_PRODUCT,
      CART_PRODUCTS
} from '../types'



export default (state,action) => {
    switch(action.type)
    {
        case GET_PRODUCTS:
            return {
                ...state,
                loading:false,
                products:action.payload
            }
            case PER_PRODUCT:
            return {
                ...state,
                loading:false,
                product:action.payload
            }
            case CATEGORY_PRODUCT:
            return {
                ...state,
                loading:false,
                categoryproducts:action.payload
            }
        case PRODUCT_ERROR:
            return {
                ...state,
                loading:false,
                errors:action.payload
            }
        case CART_PRODUCTS:
            return{
                ...state,
                loading:false,
                cart:[...action.payload]
            }
            default:
                return state; 
    }
}
