import React,{useContext,useEffect} from 'react';
import '@splidejs/splide/dist/css/themes/splide-default.min.css';

import { Splide, SplideSlide } from '@splidejs/react-splide';
import Product from './product';
import ProductContext from '../productcontextapi/productcontext'

const ProductsList = () => {
        const productcontext = useContext(ProductContext); 
        const {getproducts,productstate,cat} = productcontext;
       
        useEffect(()=>{
            getproducts();
        },[])
        useEffect(()=>{
            cat("606765e2cc4f5b486c95530b");
        },[])
        console.log(productstate)
    return (  
        <React.Fragment>
      <div className="">
      <Splide
         options={ {
            type    : 'loop',
            perPage : 1,
            autoWidth: true,
	      autoplay: true,
          interval:2000,
          focus    : 'center',
          autoHeight:true,
     
       
	      rewind: true,
          } }
      >
          {productstate.products.map((prod)=>(
                <SplideSlide>
                <img src={prod.image.base64} alt="Image 1"/>
              </SplideSlide>
              
          ))}
  
</Splide>
</div>
   
               
         <div className="container mt-3 ">
             <h1>Category</h1>
             <div className="">
      <Splide
         options={ {
            type    : 'loop',
            perPage : 1,
            autoWidth: true,
	      autoplay: true,
          interval:2000,
          focus    : 'center',
          autoHeight:true,
     
       
	      rewind: true,
          } }
      >
         {!productstate.loading && productstate.category.category.map((catgr)=>(
              <SplideSlide>
                  <div style={{height:"90px"}} key={catgr._id}>
                      <div className="border border-info" style={{height:"90px"}}>{catgr}</div>
                      </div>
              </SplideSlide>
         ))}
              
         
  
</Splide>
</div>
             <h1  >Latest Products</h1>
             <div className="row">
                  {productstate.products.length>0 ? (
                      productstate.products.map((prod)=>(
                          <Product key={prod._id} product={prod}/>
                          
                      ))   
                        

                  ):(
                      <h3> no products found</h3>
                  )}
                  

                  
             </div>
             </div>
      
          
             </React.Fragment>


    );
}
 
export default ProductsList;
