import React,{useContext,useState} from 'react';
import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'
import ProductContext from '../productcontextapi/productcontext'
import {Link} from 'react-router-dom'
const Review = () => {
    const MySwal = withReactContent(Swal)
    const productcontext = useState(ProductContext);
    const {productstate,addreview} = productcontext;
    const [formdata,setformdata] = useState({
        rating:0,
        comment:''
    })

    const onSubmit = (e)=>{
        e.preventDefault();
        MySwal.fire({
            title:<p>Review Added</p>,
            toast: true,
            position: 'top-end',
            showConfirmButton: false,
            timer:2000,
            timerProgressBar: true,
            icon: 'success',
         
        })
    }
    return ( 
           <form onSubmit={(e)=>onSubmit(e)}>
             <div className="form-group">
             <label for="exampleFormControlSelect1">Enter Rating</label>
                  <select className="form-control w-50" id="exampleFormControlSelect1" value={formdata.rating} onChange={(e)=>setformdata({...formdata,rating:e.target.value})}>
                      <option value="1">1</option>
                      <option value="2">2</option>
                      <option value="3">3</option>
                      <option value="4">4</option>
                      <option value="5">5</option>
    </select>
             </div>
             <div className="form-group">
    <label for="exampleFormControlTextarea1">Enter review</label>
    <textarea className="form-control w-75" id="exampleFormControlTextarea1" rows="3" value={formdata.comment} onChange={(e=>setformdata({...formdata,comment:e.target.value}))}></textarea>
  </div>
  <Link className="">Submit</Link>
           </form>

     );
}
 
export default Review;
