import React from 'react';
import { Link } from 'react-router-dom';
const Cartitems = ({product}) => {
    return (  
          <div >
               
                   
           <table class="table table-striped  conatiner mt-5">
   
    <thead>
    <tr>
    <Link to={`/product/${product._id}`}>
      <th ><img style={{height:'70px'}}className="" src={product.image} alt=""/></th>
      <th scope="col">{product.name}</th>
      <th scope="col">&#8377;{product.price}</th>
      <th  className="text-info" scope="col">{product.qty}</th>
      </Link>  
      <th scope="col"><i className="fa fa-trash btn btn-danger rounded btn-sm"></i></th>
    </tr>
  </thead>

           </table>
           </div>
          
       
    );
}
 
export default Cartitems;
