import React,{useContext,useEffect} from 'react';
import '@splidejs/splide/dist/css/themes/splide-default.min.css';
import Back from './cat.jpg'
import { Splide, SplideSlide } from '@splidejs/react-splide';
import Product from './product';
import ProductContext from '../productcontextapi/productcontext'
import {Link} from 'react-router-dom'

const ProductsList = () => {
   
        const productcontext = useContext(ProductContext); 
        const {getproducts,productstate,cat} = productcontext;
       
        useEffect(()=>{
            getproducts();
        },[])
        useEffect(()=>{
            cat("606765e2cc4f5b486c95530b");
        },[])
        console.log(productstate)
    return (  
        <React.Fragment>
      <div className="">
      <Splide
         options={ {
            type    : 'loop',
            perPage : 1,
            autoWidth: true,
	      autoplay: true,
          interval:2000,
          focus    : 'center',
          autoHeight:true,
     
       
	      rewind: true,
          } }
      >
          {productstate.products.map((prod)=>(
                <SplideSlide>
                <img src={prod.image.base64} alt="Image 1"/>
              </SplideSlide>
              
          ))}
  
</Splide>
</div>
   
               
         <div className="container mt-3 ">
           <h1>Categories</h1>
           <div className="">
      <Splide
         options={ {
            type    : 'loop',
            perPage : 3,
            autoWidth: true,
	      autoplay: true,
          interval:2000,
          focus    : 'center',
          autoHeight:true,
     
       
	      rewind: true,
          } }
      >
      {!productstate.loading && productstate.category.category.map((catgr)=>(
          <SplideSlide>
         <Link className=" hvr-underline-from-left  dropdown-item " to={`/category/${catgr}`}><div className="card text-center m-4  rounded" style={{width:"18rem"}}>
              <div style={{backgroundImage:`url(${Back} )`, backgroundRepeat: 'no-repeat',
        backgroundSize:"cover",
        height:'20vh',
        overflowY:'hidden',
    
      
        backgroundPosition: 'center'}}  className="card-body ">
              <h2 className="card-title text-info">{catgr}</h2>  
              </div>
              </div></Link> 
              </SplideSlide>
      ))}
     
  
</Splide>
</div>
            
             <h1  >Latest Products</h1>
             <div className="row">
                  {productstate.products.length>0 ? (
                      productstate.products.map((prod)=>(
                          <Product key={prod._id} product={prod}/>
                          
                      ))   
                        

                  ):(
                      <h3> no products found</h3>
                  )}
                  

                  
             </div>
             </div>
      
          
             </React.Fragment>


    );
}
 
export default ProductsList;
