import AddressContext from '../addresscontextapi/addresscontext'
const Addressform = () => {
    const addresscontext = useContext(AddressContext);
    const {addresstate,addaddress} = addresscontext;
    console.log(addresstate);
    const [formdata,setformdata] = useState({
        name:'',
      


   });

    
  const onSubmit = (e)=>{
      e.preventDefault();
      addaddress(formdata);
  }  
    return (  
        <div>
            <form  onSubmit={e=>onSubmit(e)} >
            <div className="form-group">
    <h3><label>Enter Your Shipping Address</label></h3>
    <input  type="text" className="form-control" required value={formdata.address} onChange={e=>setformdata({...formdata,address:e.target.value})}/>
   
  </div>
  <button type="submit" className=" rounded btn btn-outline-primary">Submit</button>
            </form>
        </div>
    );
}
 
export default Addressform;
