import React ,{useContext}from 'react';
import {Link} from 'react-router-dom'
import AuthContext from '../authcontextapi/authcontext'
import ProductContext from '../productcontextapi/productcontext'
const Summary = () => {
    const authcontext = useContext(AuthContext);
    const {authstate}= authcontext;
    const productcontext  = useContext(ProductContext);
    const {productstate} = productcontext;
    console.log(productstate);
    console.log(authstate)
    return ( 
        <div className="container mt-5">
              <div className="row">
                  <div className="col-lg-6">
                    <h2 >Shippingaddress</h2>
                      <p style={{fontSize:"20px"}}>{productstate.usercartaddress}</p>
                    <h2>paymentmethod</h2>
                    <p style={{fontSize:"20px"}}>COD-cash on delivery</p>
                    <h2>orderitems</h2>
                    {productstate.cart.map((items)=>(
                       <table class="table">
                          <tr>
                              <th>
                                <img style={{width:"50px",height:"50px"}} src={items.image} alt=""/>  
                              </th>
                              <th>
                                 {items.name} 
                              </th>
                              <th>
                                 {items.qty} 
                                 
                              </th>
                              <th>
                                 {items.price} 
                              </th>
                          </tr>

                       </table>

                    ))}

                
                  </div>
                  
                  <div className="col-lg-6">
                  <ul class="list-group">
                     <h2><center><li  className="list-group-item ">Order Summary</li></center></h2> 
                      <li style={{fontSize:"20px"}} className="list-group-item p-4 "><i class="fa fa-archive" aria-hidden="true"></i>Total Quantity 
       {productstate.cart.reduce((acc,item)=>acc+item.qty,0)}</li>
                      <li style={{fontSize:"20px"}}  className="list-group-item p-4 ml5"><i className="fa fa-money"></i>Total Price    &#8377;       {productstate.cart.reduce((acc,item)=>acc+(item.qty*item.price),0).toFixed(2)}</li>
                  </ul>
                  <Link className=" rounded btn btn-outline-info mt-3">Place Order</Link>
                  </div>
              </div>
        </div>
     );
}
 
export default Summary;
