import React,{useContext} from 'react';
import Cartitems from './cartitems'
import ProductContext from '../productcontextapi/productcontext'
const Cart = () => {
    const productcontext = useContext(ProductContext);
    const {productstate} = productcontext;
    console.log(productstate);
    return ( 
          <div>
              {productstate.cart.length>0 ? (
                  <div className="container">{productstate.cart.map((prod)=>(
                     <div className="row"> 
                     <div className="col"></div>
                    <Cartitems key={prod._id} product={prod}/>
                    </div>
                  ))}</div>
              ):(
                     <div className="container mt-5">
                         <h1>No Products in your cart</h1>
                     </div>
              )}
          </div>
  
     );
}
 
export default Cart;
