import React,{useState,useContext,useEffect} from 'react';
import axios from 'axios';
import FileBase from 'react-file-base64'
import ProductContext from '../productcontextapi/productcontext'
const EditProduct = ({match}) => {
    const productcontext = useContext(ProductContext); 
    const {productstate,update,getperproduct} = productcontext;
    console.log(productstate);
  useEffect(()=>{
       getperproduct(match.params.id)
  },[])
    const [formdata,setformdata] = useState({
         name:'',
         price:0,
         image:'',
         brand:'',
         category:'',
         countInStock:0,
         description:'',   
    

    })
  console.log(formdata.image);
   if(productstate.product)
   {
       setformdata({
         name:productstateproduct.name,
         price:product.price,
         image:product.image.base64

       })
   }
   
   
    const onSubmit = (e)=>{
        e.preventDefault();
        update(formdata,match.params.id);
   
     }
    return ( 
        <div className="mt-5 container">
            <h1>Edit Product</h1>
            <form onSubmit={(e)=>onSubmit(e)}>

                <div className="form-group mt-3">
                  <h3> <label >Name</label></h3>
                   <input type="text" className="mt-2 form-control"  placeholder="Enter Product Name" value={formdata.name} onChange={(e)=>setformdata({...formdata,name:e.target.value})} />
                   <h3><label className="mt-2">Price</label></h3>
                   <input type="number" className=" mt-2 form-control"  placeholder="Enter Product Price" value={formdata.price} onChange={(e)=>setformdata({...formdata,price:e.target.value})}/>
                   <h3><label className="mt-2">Brand</label></h3>
                   <input type="text" className=" mt-2 form-control"  placeholder="Enter Product Brand" value={formdata.brand} onChange={(e)=>setformdata({...formdata,brand:e.target.value})}/>
                   <h3><label className="mt-2">Category</label></h3>
                   <input type="text" className=" mt-2 form-control"  placeholder="Enter Product Category" value={formdata.category} onChange={(e)=>setformdata({...formdata,category:e.target.value})}/>
                   <h3><label className="mt-2">Count In Stock</label></h3>
                   <input type="number" className="mt-2 form-control"  placeholder="Enter Count in stock" value={formdata.countInStock} onChange={(e)=>setformdata({...formdata,countInStock:e.target.value})}/>
                   <h3><label className="mt-2">Description</label></h3>
                   <textarea type="text" className=" mt-2 form-control"  placeholder="Enter Product Description" value={formdata.description} onChange={(e)=>setformdata({...formdata,description:e.target.value})}/>
                   <h3><label className="mt-2">Image</label></h3>
                   <div class="form-group">
                   
                   <FileBase
                       type="file"
                       multiple={false}
                       onDone={(base64)=>setformdata({...formdata,image:base64})}
                   />
                
                   </div>
                   <button type="submit" className="  btn btn-outline-dark rounded  mt-3 ">Create</button>
                </div>
                
            </form>
        </div>
     );
}
 
export default EditProduct;
