import React,{useContext,useEffect} from 'react';
import { Splide, SplideSlide } from '@splidejs/react-splide';
import Product from './product';
import ProductContext from '../productcontextapi/productcontext'
const ProductsList = () => {
        const productcontext = useContext(ProductContext); 
        const {getproducts,productstate} = productcontext;
       
        useEffect(()=>{
            getproducts();
        },[])
        console.log(productstate)
    return (  
         <div className="container mt-3 ">
             <Splide>
                       <SplideSlide>
                       <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTJfsNsDMHf6B34m774TLKsQNpy2Ua83Yy0lA&usqp=CAU" alt="Image 1"/>
                       </SplideSlide>
                       <SplideSlide>
                       <img src="https://image.oppo.com/content/dam/oppo/common/mkt/v2-2/reno5-pro-5g-chaka/listpage/Reno5-pro-chaka-listpage-series-black-blue-v2-mobile.jpg" alt="Image 1"/>
                       </SplideSlide>
                       <SplideSlide>
                       <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTlkE1CjcDYzcO9JksNFYy1Qrt9fWxGJ5Y2HQ&usqp=CAU" alt="Image 1"/>
                       </SplideSlide>
              </Splide>
             <h1  >Latest Products</h1>
             <div className="row">
                  {productstate.products.length>0 ? (
                      productstate.products.map((prod)=>(
                          <Product key={prod._id} product={prod}/>
                          
                      ))   
                        

                  ):(
                      <h3> no products found</h3>
                  )}
             </div>
             </div>
      
          
            


    );
}
 
export default ProductsList;
