import React,{useReducer} from 'react';
import ProductContext from './productcontext'
import ProductReducer from './productreducer'
import axios from 'axios';

import {
  GET_PRODUCTS,
  PRODUCT_ERROR,
  PER_PRODUCT,
  CATEGORY_PRODUCT,
  CART_PRODUCTS,
  GET_USER_ADDRESS,
  ADD_PRODUCT
} from '../types'

const ProductState = props=>{

      const initialstate = {
          products:[],
          categoryproducts:[],
          product:'',
          cart:[],
          usercartaddress:'',
          file:'',
          loading:true,
          errors:'',
          addedproduct:''


      }

 
const [state,dispatch] = useReducer(ProductReducer,initialstate);

const getproducts = async()=>{
    try {
        
        const res = await axios.get('http://localhost:5000/api/product/');
        dispatch({
            type:GET_PRODUCTS,
            payload:res.data
        })
   
       } catch (err) {
           console.log(err.message)
           dispatch({
               type:PRODUCT_ERROR,
               
           })
    }
}
const getperproduct = async(id)=>{
    
    try {
         
        const res = await axios.get(`http://localhost:5000/api/product/${id}`);
        dispatch({
            type:PER_PRODUCT,
            payload:res.data
        })
     
   
       } catch (err) {
           console.log(err.message)
           const e = err.response.data.msg
           dispatch({
               type:PRODUCT_ERROR,
               payload:e               
           })
    }
}

const getcartproduct = async(id,qty,address)=>{
    
    try {
         
        const res = await axios.get(`http://localhost:5000/api/product/cart/${id}`);
        console.log(qty);
        
        res.data.qty = qty;
       
        console.log(res.data);
        dispatch({
            type:CART_PRODUCTS,
            payload:res.data
        })
   
       } catch (err) {
           console.log(err.message)
           const e = err.response.data.msg
           dispatch({
               type:PRODUCT_ERROR,
               payload:e               
           })
    }
}
const getaddresscart = async(address)=>{
   
    try {
        
        dispatch({
            type:GET_USER_ADDRESS,
            payload:address
        })
    } catch (error) {
        console.log(error);
    }
}
const deleteaddresscart = async()=>{
   
    try {
        
        dispatch({
            type:GET_USER_ADDRESS,
            payload:''
        })
    } catch (error) {
        console.log(error);
    }
}
const categoryproduct = async(category)=>{
    
    try {
         
        const res = await axios.get(`http://localhost:5000/api/product/category/${category}`);
        dispatch({
            type:CATEGORY_PRODUCT,
            payload:res.data
        })
   
       } catch (err) {
           console.log(err.message)
           const e = err.response.data.msg
           dispatch({
               type:PRODUCT_ERROR,
               payload:e               
           })
    }
}
const addproduct = async(formdata)=>{
    console.log(formdata);
    try {
        const config = {
            headers:{
                'Content-Type': 'application/json',
                "Access-Control-Allow-Origin": true,
                
            }    
        }
        const res = await axios.post('http://localhost:5000/api/product',formdata,config)
            dispatch({
                type:ADD_PRODUCT,
                payload:res.data
            })
    } catch (error) {
        console.log(error);
    }
}



    return (

        <ProductContext.Provider
        
           value={
               {
                   getproducts,
                   getperproduct,
                   categoryproduct,
                   getcartproduct,
                   getaddresscart,
                   deleteaddresscart,
                   addproduct,
                   productstate:state
               }
           }
         
        >
        
        
        {props.children}
        </ProductContext.Provider>
    )





}
export default ProductState;
